import React, {useState, useEffect} from 'react'
import axios from '../../axios';
import request from '../../request'
const Home = () => {
  const [movie, setMovie]=useState([]);
  
  useEffect(()=>{
    async function fetchData(){
      const request = await axios.get(request.peliculaDestacada)
      setMovie(
        request.data.results[
          Math.floor(Math.random() * request.data.results.lenght - 1)
          ]
        );
        return request;
    }
    fetchData();
  },[]);
  return (
    <>
    <header className='baneer'> {/* imagen de fondo */}
      <div className='banner_contents'>
        <div> {/* titulo */}
          <p>ORIGINAL DE <span>LITEFLIX</span></p>
          <p>TITULO DE PELICULA</p>
        </div>
        <div> {/* botones */}
          <button>REPRODUCIR</button>
          <button>MI LISTA</button>
        </div>
      </div>
    </header>
    </>
  )
}

export default Home
